	<?php $conn=mysqli_connect('localhost','root','','automatedpayroll'); if(isset($_POST['login'])) {$username=mysqli_real_escape_string($conn,$_POST["username"]); $user_pass=mysqli_real_escape_string($conn,md5($_POST["password"])); $registration=$_POST['registration']; $query="select * from ".$registration." where Username=? and Password=?"; $stmt=mysqli_stmt_init($conn); if(!mysqli_stmt_prepare($stmt,$query)){printf("error: %s\n",mysqli_error($conn)); } else {mysqli_stmt_bind_param($stmt,"ss",$username,$user_pass); mysqli_stmt_execute($stmt); $res=mysqli_stmt_get_result($stmt); $row=mysqli_num_rows($res); if($row>0) {while($result=mysqli_fetch_assoc($res)) {$data[]=$result; } print(json_encode($data)); } } } ?>